-module(lustre_fable@book).
-compile([no_auto_import, nowarn_unused_vars, nowarn_unused_function, nowarn_nomatch]).

-export([start/1]).
-export_type([book/0, model/0, args/0, msg/0]).

-if(?OTP_RELEASE >= 27).
-define(MODULEDOC(Str), -moduledoc(Str)).
-define(DOC(Str), -doc(Str)).
-else.
-define(MODULEDOC(Str), -compile([])).
-define(DOC(Str), -compile([])).
-endif.

?MODULEDOC(false).

-type book() :: {book,
        binary(),
        list(binary()),
        list(binary()),
        list({binary(), list(lustre_fable@story:story_config())})}.

-type model() :: {model,
        binary(),
        gleam@dict:dict(binary(), lustre_fable@chapter:chapter()),
        lustre_fable@route:route(),
        boolean()}.

-type args() :: {args, binary(), list(lustre_fable@chapter:chapter())}.

-type msg() :: {user_navigated_to, lustre_fable@route:route()} |
    user_toggled_sidebar.

-file("src/lustre_fable/book.gleam", 71).
?DOC(false).
-spec init(args()) -> {model(), lustre@effect:effect(msg())}.
init(Args) ->
    Route = begin
        _pipe = modem:initial_uri(),
        _pipe@1 = gleam@result:map(_pipe, fun lustre_fable@route:parse/1),
        gleam@result:unwrap(_pipe@1, index)
    end,
    _assert_subject = gleam@regexp:from_string(<<"[^a-zA-Z0-9]+"/utf8>>),
    {ok, Re} = case _assert_subject of
        {ok, _} -> _assert_subject;
        _assert_fail ->
            erlang:error(#{gleam_error => let_assert,
                        message => <<"Pattern match failed, no pattern matched the value."/utf8>>,
                        value => _assert_fail,
                        module => <<"lustre_fable/book"/utf8>>,
                        function => <<"init"/utf8>>,
                        line => 77})
    end,
    Chapters@1 = gleam@list:fold(
        erlang:element(3, Args),
        maps:new(),
        fun(Chapters, Chapter) ->
            gleam@dict:insert(
                Chapters,
                begin
                    _pipe@2 = erlang:element(2, Chapter),
                    _pipe@3 = gleam_regexp_ffi:replace(
                        Re,
                        _pipe@2,
                        <<"-"/utf8>>
                    ),
                    string:lowercase(_pipe@3)
                end,
                Chapter
            )
        end
    ),
    Model = {model, erlang:element(2, Args), Chapters@1, Route, false},
    Effect = modem:init(fun(Uri) -> _pipe@4 = Uri,
            _pipe@5 = lustre_fable@route:parse(_pipe@4),
            {user_navigated_to, _pipe@5} end),
    {Model, Effect}.

-file("src/lustre_fable/book.gleam", 107).
?DOC(false).
-spec update(model(), msg()) -> {model(), lustre@effect:effect(msg())}.
update(Model, Msg) ->
    case Msg of
        {user_navigated_to, Route} ->
            {begin
                    _record = Model,
                    {model,
                        erlang:element(2, _record),
                        erlang:element(3, _record),
                        Route,
                        erlang:element(5, _record)}
                end,
                lustre@effect:none()};

        user_toggled_sidebar ->
            {begin
                    _record@1 = Model,
                    {model,
                        erlang:element(2, _record@1),
                        erlang:element(3, _record@1),
                        erlang:element(4, _record@1),
                        not erlang:element(5, Model)}
                end,
                lustre@effect:none()}
    end.

-file("src/lustre_fable/book.gleam", 167).
?DOC(false).
-spec view_nav(gleam@dict:dict(binary(), lustre_fable@chapter:chapter())) -> lustre@vdom@vnode:element(msg()).
view_nav(Chapters) ->
    lustre@element@html:nav(
        [lustre@attribute:class(<<"contents"/utf8>>)],
        begin
            gleam@list:map(
                maps:to_list(Chapters),
                fun(_use0) ->
                    {Route, Chapter} = _use0,
                    lustre@element@html:ul(
                        [lustre@attribute:class(<<"flex flex-col gap-2"/utf8>>)],
                        [lustre@element@html:h2(
                                [],
                                [lustre@element@html:text(
                                        erlang:element(2, Chapter)
                                    )]
                            ) |
                            gleam@list:map(
                                erlang:element(3, Chapter),
                                fun(Story) ->
                                    lustre@element@html:li(
                                        [lustre@attribute:class(<<"ml-2"/utf8>>)],
                                        [lustre@element@html:a(
                                                [lustre@attribute:href(
                                                        <<<<<<"/chapter/"/utf8,
                                                                    Route/binary>>/binary,
                                                                "/story/"/utf8>>/binary,
                                                            (erlang:element(
                                                                3,
                                                                Story
                                                            ))/binary>>
                                                    )],
                                                [lustre@element@html:text(
                                                        erlang:element(2, Story)
                                                    )]
                                            )]
                                    )
                                end
                            )]
                    )
                end
            )
        end
    ).

