?DOC(false).
-spec view_chapter(
    binary(),
    gleam@dict:dict(binary(), lustre_fable@chapter:chapter())
) -> lustre@vdom@vnode:element(msg()).
view_chapter(C, Chapters) ->
    _assert_subject = gleam_stdlib:map_get(Chapters, C),
    {ok, Chapter} = case _assert_subject of
        {ok, _} -> _assert_subject;
        _assert_fail ->
            erlang:error(#{gleam_error => let_assert,
                        message => <<"Pattern match failed, no pattern matched the value."/utf8>>,
                        value => _assert_fail,
                        module => <<"lustre_fable/book"/utf8>>,
                        function => <<"view_chapter"/utf8>>,
                        line => 186})
    end,
    lustre@element@html:'div'(
        [lustre@attribute:class(<<"grid grid-cols-3 gap-8"/utf8>>)],
        begin
            gleam@list:map(
                erlang:element(3, Chapter),
                fun(Story) ->
                    Href = <<<<<<"/chapter/"/utf8, C/binary>>/binary,
                            "/story/"/utf8>>/binary,
                        (erlang:element(3, Story))/binary>>,
                    lustre@element@html:a(
                        [lustre@attribute:href(Href),
                            lustre@attribute:class(
                                <<"p-2 border rounded"/utf8>>
                            )],
                        [lustre@element@html:text(erlang:element(2, Story))]
                    )
                end
            )
        end
    ).

