?DOC(false).
-spec view_story(
    binary(),
    binary(),
    gleam@dict:dict(binary(), lustre_fable@chapter:chapter())
) -> lustre@vdom@vnode:element(msg()).
view_story(C, S, Chapters) ->
    _assert_subject = gleam_stdlib:map_get(Chapters, C),
    {ok, Chapter} = case _assert_subject of
        {ok, _} -> _assert_subject;
        _assert_fail ->
            erlang:error(#{gleam_error => let_assert,
                        message => <<"Pattern match failed, no pattern matched the value."/utf8>>,
                        value => _assert_fail,
                        module => <<"lustre_fable/book"/utf8>>,
                        function => <<"view_story"/utf8>>,
                        line => 202})
    end,
    _assert_subject@1 = gleam@list:find(
        erlang:element(3, Chapter),
        fun(Story) -> erlang:element(3, Story) =:= S end
    ),
    {ok, Story@1} = case _assert_subject@1 of
        {ok, _} -> _assert_subject@1;
        _assert_fail@1 ->
            erlang:error(#{gleam_error => let_assert,
                        message => <<"Pattern match failed, no pattern matched the value."/utf8>>,
                        value => _assert_fail@1,
                        module => <<"lustre_fable/book"/utf8>>,
                        function => <<"view_story"/utf8>>,
                        line => 203})
    end,
    lustre@element:element(
        erlang:element(4, Story@1),
        [lustre@attribute:class(<<"flex-1"/utf8>>)],
        []
    ).

