-file("src/lustre_fable/book.gleam", 36).
?DOC(false).
-spec start(book()) -> {ok, nil} | {error, lustre:error()}.
start(Book) ->
    gleam@result:'try'(
        (gleam@list:try_map(
            erlang:element(5, Book),
            fun(Chapter) ->
                lustre_fable@chapter:init(
                    erlang:element(1, Chapter),
                    erlang:element(2, Chapter),
                    erlang:element(3, Book),
                    erlang:element(4, Book)
                )
            end
        )),
        fun(Chapters) ->
            App = lustre:application(fun init/1, fun update/2, fun view/1),
            Args = {args, erlang:element(2, Book), Chapters},
            gleam@result:'try'(
                lustre:start(App, <<"#app"/utf8>>, Args),
                fun(_) -> {ok, nil} end
            )
        end
    ).
